import{c as a}from"./app.d3b1c066.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const h={},r=a('<h1 id="_1\u6708\u4E0B\u534A" tabindex="-1"><a class="header-anchor" href="#_1\u6708\u4E0B\u534A" aria-hidden="true">#</a> 1\u6708\u4E0B\u534A</h1><h2 id="\u8D44\u6E90\u65E5\u5386\u4E0E\u9879\u76EE\u65E5\u5386" tabindex="-1"><a class="header-anchor" href="#\u8D44\u6E90\u65E5\u5386\u4E0E\u9879\u76EE\u65E5\u5386" aria-hidden="true">#</a> \u8D44\u6E90\u65E5\u5386\u4E0E\u9879\u76EE\u65E5\u5386</h2><h3 id="\u8D44\u6E90\u65E5\u5386" tabindex="-1"><a class="header-anchor" href="#\u8D44\u6E90\u65E5\u5386" aria-hidden="true">#</a> \u8D44\u6E90\u65E5\u5386</h3><blockquote><p>\u8D44\u6E90\u65E5\u5386\u4E2D\u5305\u62EC\u4E86\u8D44\u6E90\u7684\u53EF\u7528\u6027\u3001\u8D44\u6E90\u7684\u7C7B\u578B\u3001\u8D44\u6E90\u7684\u6027\u8D28\u3001\u6BCF\u4E00\u4E2A\u8D44\u6E90\u90FD\u4F1A\u6709\u4E00\u4E2A\u8D44\u6E90\u65E5\u5386\u3002\u4ED6\u4F1A\u663E\u793A\u4E00\u4E2A\u8D44\u6E90\u65F6\u4E48\u65F6\u5019\u53EF\u7528\uFF0C\u5E76\u4E14\u53EF\u4EE5\u7528\u591A\u4E45\u3002</p></blockquote><p>\u5E38\u7528\u7684\u8D44\u6E90\u53EF\u7528\u6027\uFF1A\u65F6\u95F4\u4E0A\u662F\u5426\u53EF\u7528\uFF0C\u6280\u80FD\u4E0A\u662F\u5426\u7B26\u5408\u8981\u6C42\u3002</p><p>\u6BD4\u5982\u8BF4\uFF1A\u9700\u8981\u4E00\u4E2A\u7A0B\u5E8F\u5458\uFF0C\u9700\u8981\u5468\u4E00\u5230\u5468\u4E94\u4E3A\u9879\u76EE\u5DE5\u4F5C\uFF0C\u7136\u540E\u518D\u770B\u80FD\u529B\u4E0A\u662F\u5426\u5339\u914D\u3002</p><p>\u4EBA\u672C\u8EAB\u5C31\u662F\u8D44\u6E90</p><h3 id="\u9879\u76EE\u65E5\u5386" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u65E5\u5386" aria-hidden="true">#</a> \u9879\u76EE\u65E5\u5386</h3><blockquote><p>\u9879\u76EE\u65E5\u5386\u5F53\u4E2D\u8BB0\u5F55\u4E86\u53EF\u4EE5\u5F00\u5C55\u9879\u76EE\u7684\u5DE5\u4F5C\u65E5\u548C\u5DE5\u4F5C\u73ED\u6B21\u3002</p></blockquote><p>\u5728\u4E00\u4E2A\u9879\u76EE\u4E2D\uFF0C\u4E0D\u4EC5\u4EC5\u662F\u7528\u4E00\u4E2A\u8D44\u6E90\uFF0C\u53EF\u80FD\u8981\u4F7F\u7528\u591A\u4E2A\u8D44\u6E90\u9700\u8981\u7EFC\u5408\u8003\u8651\u591A\u4E2A\u8D44\u6E90\u7684\u5236\u7EA6\u56E0\u7D20\uFF08\u4F8B\u5982\uFF1A\u65F6\u95F4\uFF09</p><p>\u4E0D\u540C\u8D44\u6E90\u7684\u4EA4\u96C6\u4EE5\u53CA\u5BA2\u89C2\u6761\u4EF6\u5BF9\u4E8E\u9879\u76EE\u5B9E\u65BD\u7684\u5F71\u54CD\u4F8B\u5982\u5BA2\u89C2\u7269\u7406\u73AF\u5883\u7684\u5F71\u54CD</p><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><ul><li><p>\u8D44\u6E90\u65E5\u5386\u4EE3\u8868\u4E86\u67D0\u4E2A\u8D44\u6E90\u7684\u5DE5\u4F5C\u65F6\u95F4\u548C\u5DE5\u4F5C\u73ED\u6B21\u3002</p></li><li><p>\u9879\u76EE\u65E5\u5386\u4EE3\u8868\u4E86\u6574\u4E2A\u9879\u76EE\u7684\u5DE5\u4F5C\u65F6\u95F4\u548C\u5DE5\u4F5C\u73ED\u6B21\u3002</p></li><li><p>\u4E00\u4E2A\u9879\u76EE\u53EF\u80FD\u6709\u591A\u4E2A\u8D44\u6E90\u65E5\u5386\uFF0C\u4F46\u6709\u4E14\u53EA\u6709\u4E00\u4E2A\u9879\u76EE\u65E5\u5386\u3002</p></li></ul><h2 id="\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212" aria-hidden="true">#</a> \u6C9F\u901A\u7BA1\u7406\u8BA1\u5212</h2><blockquote><p>\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212\u662F\u9879\u76EE\u7BA1\u7406\u5341\u4E2A\u5B50\u8BA1\u5212\u4E4B\u4E00\uFF0C\u5B83\u662F\u89C4\u5212\u7BA1\u7406\u6C9F\u901A\u7684\u8F93\u51FA\u3002</p></blockquote><h3 id="\u5305\u542B\u7684\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u5305\u542B\u7684\u5185\u5BB9" aria-hidden="true">#</a> \u5305\u542B\u7684\u5185\u5BB9</h3><p>\u91CC\u9762\u5305\u542B\u4E86\u5E72\u7CFB\u4EBA\u7684\u6C9F\u901A\u9700\u6C42\uFF0C\u9700\u8981\u6C9F\u901A\u7684\u4FE1\u606F\uFF0C\u9700\u8981\u6C9F\u901A\u7684\u8BED\u8A00\u3001\u683C\u5F0F\u3001\u5185\u5BB9\u3001\u8BE6\u7EC6\u4FE1\u606F\u7B49\u7B49\u3002</p><h3 id="\u5177\u4F53\u5B9E\u65BD" tabindex="-1"><a class="header-anchor" href="#\u5177\u4F53\u5B9E\u65BD" aria-hidden="true">#</a> \u5177\u4F53\u5B9E\u65BD</h3><p>\u8FD8\u6709\u4E0A\u62A5\u6D41\u7A0B\u3001\u53D1\u5E03\u4FE1\u606F\u7684\u539F\u56E0\u3001\u53D1\u5E03\u4FE1\u606F\u7684\u9891\u7387\u3001\u4EE5\u53CA\u662F\u8C01\u53D1\u5E03\u7684\u3001\u8C01\u63A5\u53D7\u7684\u3001\u4F20\u9012\u4FE1\u606F\u7684\u6280\u672F\u548C\u65B9\u6CD5\u3001\u4F20\u9012\u4FE1\u606F\u7684\u65F6\u95F4\u548C\u9884\u7B97\u3002</p><h3 id="\u6C9F\u901A\u7BA1\u7406\u91CD\u70B9" tabindex="-1"><a class="header-anchor" href="#\u6C9F\u901A\u7BA1\u7406\u91CD\u70B9" aria-hidden="true">#</a> \u6C9F\u901A\u7BA1\u7406\u91CD\u70B9</h3><ul><li><p>\u4EC0\u4E48\u5185\u5BB9</p></li><li><p>\u4E3A\u4EC0\u4E48\u8981\u6C9F\u901A</p></li><li><p>\u4EC0\u4E48\u65F6\u95F4\u505A\u6C9F\u901A</p></li><li><p>\u5728\u54EA\u505A\u6C9F\u901A</p></li><li><p>\u5982\u679C\u6C9F\u901A</p></li></ul><h3 id="\u4F5C\u7528" tabindex="-1"><a class="header-anchor" href="#\u4F5C\u7528" aria-hidden="true">#</a> \u4F5C\u7528</h3><p>\u6C9F\u901A\u7684\u6700\u5927\u4F5C\u7528\u65F6\u7528\u6765\u4EBA\u5458\u4E4B\u95F4\u7684\u6C9F\u901A\uFF08\u8D44\u6E90\u4E4B\u95F4\u7684\u4EA4\u4E92-\u7C7B\u4F3C\u4E8E\u4E00\u79CD\u8D44\u6E90\u7684\u6D3B\u5316\uFF09</p><h2 id="\u6C9F\u901A\u65B9\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u6C9F\u901A\u65B9\u6CD5" aria-hidden="true">#</a> \u6C9F\u901A\u65B9\u6CD5</h2><blockquote><p>\u4E09\u79CD\u6C9F\u901A\u65B9\u6CD5\uFF1A\u63A8\u5F0F\u6C9F\u901A\u3001\u62C9\u5F0F\u6C9F\u901A\u548C\u4E92\u52A8\u5F0F\u6C9F\u901A\u3002</p></blockquote><h3 id="\u63A8\u5F0F\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u63A8\u5F0F\u6C9F\u901A" aria-hidden="true">#</a> \u63A8\u5F0F\u6C9F\u901A</h3><blockquote><p>\u786E\u4FDD\u4FE1\u606F\u88AB\u53D1\u9001\uFF0C\u4F46\u662F\u4E0D\u786E\u5B9A\u4FE1\u606F\u88AB\u63A5\u53D7\uFF0C\u4E5F\u4E0D\u786E\u5B9A\u5B83\u88AB\u7406\u89E3\uFF0C\u6BD4\u5982\u53D1\u77ED\u4FE1\uFF0C\u53D1\u5C0F\u5E7F\u544A\uFF0C\u53D1\u90AE\u4EF6\u7B49\u7B49\u3002</p><p>\u4EE5\u4E3B\u8981\u635F\u8017\u65F6\u95F4\u53D1\u9001\u65B9\u7684\u65F6\u95F4\u6210\u672C\u4E3A\u4EE3\u4EF7</p></blockquote><h3 id="\u62C9\u5F0F\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u62C9\u5F0F\u6C9F\u901A" aria-hidden="true">#</a> \u62C9\u5F0F\u6C9F\u901A</h3><blockquote><p>\u4FE1\u606F\u7684\u63A5\u53D7\u8005\u4E3B\u52A8\u6765\u8BBF\u95EE\u4FE1\u606F\u6216\u8005\u662F\u83B7\u53D6\u4FE1\u606F\u3002</p><p>\u4EE5\u4E3B\u8981\u635F\u8017\u4FE1\u606F\u63A5\u6536\u65B9\u7684\u65F6\u95F4\u6210\u672C\u4E3A\u4EE3\u4EF7\u3002</p></blockquote><p>\u628A\u4FE1\u606F\u653E\u5728\u8FD9\uFF0C\u8BA9\u6709\u9700\u8981\u7684\u4EBA\u4E3B\u52A8\u6765\u83B7\u53D6\u3002\u4F8B\u5982\u4F01\u4E1A\u5185\u8BAD\u7F51\u3001\u5728\u7EBF\u57F9\u8BAD\u7B49\u7B49\u3002</p><p>\u4EC0\u4E48\u65F6\u5019\u4F7F\u7528\uFF1A\u6709\u5927\u91CF\u53D7\u4F17\u3001\u6216\u8005\u662F\u6709\u5927\u91CF\u7684\u4FE1\u606F\u9700\u8981\u4F20\u9012\u65F6\uFF0C\u63A8\u8350\u62C9\u5F0F\u6C9F\u901A\u3002</p><h3 id="\u4E92\u52A8\u5F0F\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u4E92\u52A8\u5F0F\u6C9F\u901A" aria-hidden="true">#</a> \u4E92\u52A8\u5F0F\u6C9F\u901A</h3><blockquote><p>\u4FE1\u606F\u7684\u5B9E\u65F6\u4EA4\u6362\uFF0C\u5C31\u662F\u4E92\u52A8\u5F0F\u7684\u6C9F\u901A\uFF0C\u53C8\u63A8\u53C8\u62C9\u3002</p><p>\u4EE5\u635F\u8017\u4FE1\u606F\u7ED9\u51FA\u65B9\u548C\u4FE1\u606F\u63A5\u6536\u65B9\u7684\u65F6\u95F4\u6210\u672C\u4E3A\u4EE3\u4EF7</p></blockquote><p>\u6BD4\u5982\u8BF4\u5F00\u4F1A\u3001\u6253\u7535\u8BDD\u7B49\u7B49\u3002</p><h3 id="\u603B\u7ED3-1" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3-1" aria-hidden="true">#</a> \u603B\u7ED3</h3><p>\u6548\u679C\u6700\u597D\u7684\u5F53\u7136\u662F\u4E92\u52A8\u5F0F\u6C9F\u901A\uFF0C\u56E0\u4E3A\u4FE1\u606F\u53D1\u9001\u4E4B\u540E\u53EF\u4EE5\u5F97\u5230\u53CD\u9988\uFF0C\u8FD9\u662F\u4E00\u79CD\u6700\u6709\u6548\u7684\u6C9F\u901A\u65B9\u5F0F\u3002\u4F46\u662F\u4EE5\u635F\u8017\u65F6\u95F4\u6210\u672C\u7684\u4EE3\u4EF7\u83B7\u53D6\u6700\u4F18\u7684\u6C9F\u901A\u65B9\u6848\uFF0C\u56E0\u6B64\u4E0D\u9002\u7528\u591A\u5BF9\u8C61\u7684\u4E92\u52A8\u6C9F\u901A\u3002</p><h2 id="\u98CE\u9669" tabindex="-1"><a class="header-anchor" href="#\u98CE\u9669" aria-hidden="true">#</a> \u98CE\u9669</h2><blockquote><p>\u98CE\u9669\u662F\u6307\u8FD8\u6CA1\u6709\u53D1\u751F\u7684\u4E8B\u4EF6\u3002</p></blockquote><blockquote><p>\u98CE\u9669\u5177\u6709\u4E24\u9762\u6027\uFF0C\u79EF\u6781\u7684\u98CE\u9669\u662F\u673A\u4F1A\uFF0C\u6D88\u6781\u7684\u98CE\u9669\u662F\u5A01\u80C1\u3002</p></blockquote><p>\u98CE\u9669\u662F\u9879\u76EE\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u72EC\u7279\u6027\u5BFC\u81F4\u9879\u76EE\u7684\u4E0D\u786E\u5B9A\u6027\uFF0C\u800C\u4E0D\u786E\u5B9A\u6027\u53C8\u4F1A\u5F15\u53D1\u9879\u76EE\u7684\u98CE\u9669\uFF0C\u6240\u4EE5\u98CE\u9669\u662F\u4E00\u79CD\u4E0D\u786E\u5B9A\u6027\u7684\u4E8B\u4EF6\uFF0C\u4E00\u65E6\u53D1\u751F\u5C31\u4F1A\u5BF9\u9879\u76EE\u9020\u6210\u79EF\u6781\u6216\u8005\u6D88\u6781\u7684\u5F71\u54CD\u3002</p><blockquote><p>\u95EE\u9898\u662F\u5DF2\u77E5\u7684\u98CE\u9669</p></blockquote><p>\u51E1\u662F\u80FD\u63D0\u524D\u8BC6\u522B\u5E76\u4E14\u8BB0\u5F55\u5728\u98CE\u9669\u767B\u8BB0\u518C\u6216\u8005\u98CE\u9669\u62A5\u544A\u5F53\u4E2D\u7684\uFF0C\u5E76\u4E14\u89C4\u5212\u4E86\u5E94\u5BF9\u63AA\u65BD\u7684\u53EB\u505A\u5DF2\u77E5\u98CE\u9669\u3002\u5DF2\u77E5\u98CE\u9669\u4E00\u65E6\u53D1\u751F\u4E86\uFF0C\u53EF\u4EE5\u7528\u5E94\u6025\u50A8\u5907\u6765\u8FDB\u884C\u5E94\u5BF9\u3002\u51E1\u662F\u90A3\u4E9B\u4E0D\u80FD\u63D0\u524D\u8BC6\u522B\u7684\uFF0C\u53EB\u505A\u672A\u77E5\u98CE\u9669\u3002\u672A\u77E5\u98CE\u9669\u5982\u679C\u53D1\u751F\uFF0C\u53EA\u80FD\u7528\u7BA1\u7406\u50A8\u5907\u6765\u8FDB\u884C\u5E94\u5BF9\uFF0C\u6240\u4EE5\u641E\u5B9A\u98CE\u9669\u9700\u8981\u9760\u50A8\u5907\u3002</p><p>\u5982\u679C\u80FD\u9002\u5F53\u7684\u8FDB\u884C\u98CE\u9669\u7BA1\u7406\uFF0C\u53EF\u4EE5\u63D0\u9AD8\u9879\u76EE\u6210\u529F\u7684\u6982\u7387\u3002</p><ul><li><p>\u9879\u76EE\u98CE\u9669\u7BA1\u7406\u8BA1\u5212\u5305\u62EC\uFF1A\u76F8\u5173\u65B9\u98CE\u9669\u504F\u597D\u3002</p></li><li><p>\u98CE\u9669\u767B\u8BB0\u518C\u4E0D\u5305\u62EC\u98CE\u9669\u62A5\u544A\u3002</p></li></ul><h2 id="\u5E94\u6025\u8BA1\u5212\u4E0E\u5F39\u56DE\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u5E94\u6025\u8BA1\u5212\u4E0E\u5F39\u56DE\u8BA1\u5212" aria-hidden="true">#</a> \u5E94\u6025\u8BA1\u5212\u4E0E\u5F39\u56DE\u8BA1\u5212</h2><h3 id="\u5E94\u6025\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u5E94\u6025\u8BA1\u5212" aria-hidden="true">#</a> \u5E94\u6025\u8BA1\u5212</h3><blockquote><p>\u5E94\u6025\u8BA1\u5212\u662F\u6307\u4E00\u4E9B\u7ECF\u8BBE\u8BA1\u4EC5\u5728\u7279\u5B9A\u6761\u4EF6\u53D1\u751F\u65F6\u624D\u91C7\u7528\u7684\u4E13\u95E8\u8BA1\u5212\u8FC7\u7684\u5E94\u5BF9\u63AA\u65BD\uFF0C\u5B83\u53C8\u53EB\u5E94\u6025\u5E94\u5BF9\u7B56\u7565\u6216\u8005\u662F\u5E94\u6025\u9884\u6848\u3002</p></blockquote><p>\u5BF9\u4E8E\u67D0\u4E9B\u98CE\u9669\uFF0C\u5982\u679C\u5411\u56E2\u961F\u76F8\u4FE1\u5B83\u53D1\u751F\u524D\u4F1A\u6709\u5145\u5206\u7684\u9884\u8B66\u4FE1\u53F7\uFF0C\u5F53\u51FA\u73B0\u4E86\u8FD9\u4E9B\u9884\u8B66\u4FE1\u53F7\u548C\u7279\u5B9A\u6761\u4EF6\u65F6\uFF0C\u5C31\u5E94\u8BE5\u6267\u884C\u5E94\u5BF9\u7B56\u7565\u3002</p><h3 id="\u5F39\u56DE\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u5F39\u56DE\u8BA1\u5212" aria-hidden="true">#</a> \u5F39\u56DE\u8BA1\u5212</h3><blockquote><p>\u5F39\u56DE\u8BA1\u5212\u53C8\u53EB\u9000\u56DE\u8BA1\u5212</p></blockquote><p>\u5728\u98CE\u9669\u53D1\u751F\u5E76\u4E14\u4E3B\u8981\u7684\u5E94\u5BF9\u63AA\u65BD\u65E0\u6548\u65F6\u624D\u4F7F\u7528\u7684\u5907\u9009\u5E94\u5BF9\u8BA1\u5212\uFF0C\u6240\u4EE5\u5F39\u56DE\u8BA1\u5212\u4E00\u5B9A\u662F\u5728A\u8BA1\u5212\u65E0\u6548\u65F6\u624D\u4F1A\u91C7\u53D6\u7684B\u8BA1\u5212</p><h3 id="\u603B\u7ED3-2" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3-2" aria-hidden="true">#</a> \u603B\u7ED3</h3>',52);function i(d,p){return r}var c=e(h,[["render",i]]);e