import{c as e}from"./app.1e059750.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const o={},h=e('<h1 id="\u516C\u4F17\u53F7\u5B66\u4E60" tabindex="-1"><a class="header-anchor" href="#\u516C\u4F17\u53F7\u5B66\u4E60" aria-hidden="true">#</a> \u516C\u4F17\u53F7\u5B66\u4E60</h1><h2 id="\u9879\u76EE" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE" aria-hidden="true">#</a> \u9879\u76EE</h2><blockquote><p>\u9879\u76EE\u662F\u4E3A\u521B\u9020\u72EC\u7279\u7684\u4EA7\u54C1\uFF0C\u670D\u52A1\u6216\u6210\u679C\u800C\u8FDB\u884C\u7684\u4E34\u65F6\u6027\u7684\u5DE5\u4F5C\u3002</p></blockquote><h3 id="\u9879\u76EE\u7684\u7279\u6027" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7684\u7279\u6027" aria-hidden="true">#</a> \u9879\u76EE\u7684\u7279\u6027</h3><ul><li><p>\u4E34\u65F6\u6027\uFF08\u9879\u76EE\u6709\u660E\u786E\u7684\u5F00\u59CB\u548C\u7ED3\u675F\uFF09</p></li><li><p>\u72EC\u7279\u6027\uFF08\u9879\u76EE\u53EF\u4EE5\u521B\u9020\u72EC\u7279\u7684\u4EA7\u54C1\uFF0C\u670D\u52A1\u6216\u6210\u679C\uFF09</p></li><li><p>\u6E10\u8FDB\u660E\u7EC6\u6027\uFF08\u6E10\u8FDB\u660E\u7EC6\u6027\u662F\u638C\u63E1\u7684\u4FE1\u606F\u8D8A\u6765\u8D8A\u591A\uFF0C\u4F30\u7B97\u8D8A\u6765\u8D8A\u5177\u4F53\uFF0C\u4E0D\u65AD\u6301\u7EED\u7684\u6539\u8FDB\u548C\u7EC6\u5316\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u3002\uFF09</p></li></ul><h3 id="\u9879\u76EE\u4E3E\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u4E3E\u4F8B" aria-hidden="true">#</a> \u9879\u76EE\u4E3E\u4F8B</h3><p>\u6709\u76EE\u7684\u7684\u8FBE\u6210\u67D0\u4EF6\u4E8B\u5747\u53EF\u89C6\u5176\u4E3A\u9879\u76EE</p><h3 id="\u53EF\u4EA4\u4ED8\u6210\u679C" tabindex="-1"><a class="header-anchor" href="#\u53EF\u4EA4\u4ED8\u6210\u679C" aria-hidden="true">#</a> \u53EF\u4EA4\u4ED8\u6210\u679C</h3><blockquote><p>\u4EA7\u54C1\u670D\u52A1\u6216\u6210\u679C\u4E5F\u53EF\u53EB\u4F5C\u53EF\u4EA4\u4ED8\u6210\u679C\uFF0C\u6240\u4EE5\u53EF\u4EA4\u4ED8\u6210\u679C\u662F\u72EC\u7279\u7684\u3002</p></blockquote><ul><li><p>\u6709\u5F62\u7684\uFF08\u7269\u7406\u610F\u4E49\u4E0A\u7684\u5B58\u5728\uFF09</p></li><li><p>\u65E0\u5F62\u7684\uFF08\u7C7B\u4F3C\u4E8E\u77E5\u8BC6\u4E4B\u7C7B\u7684\uFF09</p></li><li><p>\u62D3\u5C55</p></li></ul><p>\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u72EC\u7279\u6027 =&gt; \u9879\u76EE\u7684\u8FC7\u7A0B\u4E2D\u5B58\u5728\u4E0D\u786E\u5B9A =&gt; \u9879\u76EE\u8981\u505A\u597D\u98CE\u9669\u7BA1\u7406</p><h2 id="\u9879\u76EE\u96C6" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u96C6" aria-hidden="true">#</a> \u9879\u76EE\u96C6</h2><blockquote><p>\u9879\u76EE\u96C6\u662F\u4E00\u7EC4\u4E92\u76F8\u5173\u8054\u4E14\u88AB\u534F\u8C03\u7BA1\u7406\u7684\u9879\u76EE\u3001\u5B50\u9879\u76EE\u548C\u9879\u76EE\u96C6\u6D3B\u52A8\uFF0C\u4EE5\u4FBF\u83B7\u5F97\u5206\u522B\u7BA1\u7406\u6240\u65E0\u6CD5\u83B7\u5F97\u7684\u5229\u76CA\u3002</p></blockquote><h3 id="\u7279\u6027" tabindex="-1"><a class="header-anchor" href="#\u7279\u6027" aria-hidden="true">#</a> \u7279\u6027</h3><ul><li><p>\u5173\u8054\u6027</p></li><li><p>\u4F9D\u8D56\u5173\u7CFB</p></li></ul><h2 id="\u9879\u76EE\u7EC4\u5408" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7EC4\u5408" aria-hidden="true">#</a> \u9879\u76EE\u7EC4\u5408</h2><blockquote><p>\u9879\u76EE\u7EC4\u5408\u662F\u6307\u4E3A\u5B9E\u73B0\u6218\u7565\u76EE\u6807\u800C\u7EC4\u5408\u5728\u4E00\u8D77\u7BA1\u7406\u9879\u76EE\u3001\u9879\u76EE\u96C6\u3001\u5B50\u9879\u76EE\u7EC4\u5408\u548C\u8FD0\u8425\u5DE5\u4F5C\u3002</p></blockquote><blockquote><p>\u53EF\u80FD\u6709\u4F9D\u8D56\u5173\u7CFB\uFF5C\u53EF\u80FD\u6709\u76F4\u63A5\u8054\u7CFB\uFF5C\u53EF\u80FD\u6709\u5173\u8054\u6027</p></blockquote><h2 id="\u7279\u6027-1" tabindex="-1"><a class="header-anchor" href="#\u7279\u6027-1" aria-hidden="true">#</a> \u7279\u6027</h2><ul><li>\u9879\u76EE\u4E4B\u95F4\u56E0\u4F18\u5148\u7EA7\u7684\u4E0D\u540C\u800C\u5F7C\u6B64\u7ADE\u4E89</li></ul><blockquote><p>\u5F15\u7533\u51FA\u7684\u601D\u60F3\uFF08\u8D44\u6E90 =&gt; \u4EA7\u54C1\uFF09</p></blockquote><ul><li>\u9879\u76EE\u5212\u5206\u7684\u4F5C\u7528\u57DF</li></ul><blockquote><p>(\u9879\u76EE\u7EC4\u5408 =&gt; \u9879\u76EE\u96C6 =&gt; \u9879\u76EE)</p></blockquote><h2 id="\u9879\u76EE\u751F\u547D\u5468\u671F" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u751F\u547D\u5468\u671F" aria-hidden="true">#</a> \u9879\u76EE\u751F\u547D\u5468\u671F</h2><blockquote><p>\u4E94\u79CD\u751F\u547D\u5468\u671F\uFF1A\u9884\u6D4B\u578B\u3001\u8FED\u4EE3\u578B\u3001\u589E\u91CF\u578B\u3001\u9002\u5E94\u578B\u3001\u6DF7\u5408\u578B\u3002</p></blockquote><h3 id="\u9884\u6D4B\u578B" tabindex="-1"><a class="header-anchor" href="#\u9884\u6D4B\u578B" aria-hidden="true">#</a> \u9884\u6D4B\u578B</h3><blockquote><p>\u9884\u6D4B\u578B\u751F\u547D\u5468\u671F\uFF0C\u4E5F\u53EB\u5B8C\u5168\u8BA1\u5212\u578B\u3002\u5B8C\u5168\u6309\u7167\u8BA1\u5212\u6765\u505A\uFF0C\u6574\u4E2A\u9879\u76EE\u7684\u8FC7\u7A0B\u4E2D<strong>\u6CA1\u6709\u53D8\u66F4</strong>\u3002<strong>\u9002\u7528\u4E8E\u5BF9\u9879\u76EE\u975E\u5E38\u719F\u6089\uFF0C\u975E\u5E38\u4E86\u89E3</strong></p></blockquote><blockquote><p>\u4E00\u6B21\u5B8C\u6210\uFF0C\u4E0D\u9700\u8981\u989D\u5916\u8D44\u6E90\uFF0C\u76EE\u7684\u660E\u786E\uFF0C\u5B8C\u6210\u76EE\u6807\u3002</p></blockquote><h3 id="\u8FED\u4EE3\u578B" tabindex="-1"><a class="header-anchor" href="#\u8FED\u4EE3\u578B" aria-hidden="true">#</a> \u8FED\u4EE3\u578B</h3><blockquote><p>\u8FED\u4EE3\u578B\u751F\u547D\u5468\u671F\u3002\u901A\u8FC7\u4E0D\u65AD\u7684\u5FAA\u73AF\u6D3B\u52A8\u6765\u4F18\u5316\u4EA7\u54C1\u529F\u80FD\uFF0C\u6BCF\u4E00\u8F6E\u8FED\u4EE3\u5C06\u4EA7\u54C1\u529F\u80FD\u505A\u5230\u6700\u4F18\u3002</p></blockquote><blockquote><p>\u4E00\u6B21\u5B8C\u6210\uFF0C\u591A\u6B21\u4FEE\u6539\uFF0C\u9700\u8981\u8D44\u6E90\uFF0C\u5F53\u6B21\u76EE\u7684\u660E\u786E\uFF08\u6839\u636E\u7ED3\u679C\u6765\u8C03\u6574\u4E0B\u6B21\u76EE\u7684\uFF09\uFF0C\u5B8C\u6210\u76EE\u6807\u3002</p></blockquote><h3 id="\u589E\u91CF\u578B" tabindex="-1"><a class="header-anchor" href="#\u589E\u91CF\u578B" aria-hidden="true">#</a> \u589E\u91CF\u578B</h3><blockquote><p>\u589E\u91CF\u578B\u751F\u547D\u5468\u671F\u3002\u5728\u7279\u5B9A\u7684\u65F6\u95F4\u5185\u4E0D\u65AD\u7684\u589E\u52A0\u4EA7\u54C1\u7684\u529F\u80FD\uFF0C\u6BCF\u4E00\u8F6E\u8FED\u4EE3\u90FD\u5728\u4E0A\u4E00\u6B21\u57FA\u7840\u4E0A\u9762\u65B0\u589E\u529F\u80FD\u3002</p></blockquote><blockquote><p>\u591A\u6B21\u5B8C\u6210\uFF0C\u9700\u8981\u8D44\u6E90\uFF0C\u5F53\u6B21\u76EE\u7684\u660E\u786E\uFF08\u6839\u636E\u7ED3\u679C\u6765\u8C03\u6574\u4E0B\u6B21\u76EE\u7684\uFF09\uFF0C\u5B8C\u6210\u76EE\u6807\u3002</p></blockquote><h3 id="\u9002\u5E94\u578B" tabindex="-1"><a class="header-anchor" href="#\u9002\u5E94\u578B" aria-hidden="true">#</a> \u9002\u5E94\u578B</h3><blockquote><p>\u9002\u5E94\u578B\u751F\u547D\u5468\u671F\uFF0C\u53C8\u53EB\u654F\u6377\u65B9\u6CD5\uFF0C\u76F8\u5F53\u4E8E\u662F\u8FED\u4EE3\u578B\u548C\u589E\u91CF\u578B\u7684\u6DF7\u5408\u3002\u65E2\u8981\u4F18\u5316\u4EA7\u54C1\uFF0C\u53C8\u8981\u589E\u52A0\u529F\u80FD\u3002</p></blockquote><blockquote><p>\u5BF9\u8981\u4EA4\u4ED8\u7684\u6700\u7EC8\u4EA7\u54C1\u548C\u7ED3\u679C\u96BE\u4EE5\u4E8B\u5148\u786E\u5B9A\u3002\u5728\u9879\u76EE\u7BA1\u7406\u7684\u8FC7\u7A0B\u4E2D\u7ECF\u5E38\u6709\u53D8\u5316\uFF0C\u9700\u8981\u5E94\u5BF9\u5FEB\u901F\u53D8\u5316\u7684\u73AF\u5883\uFF0C\u76F8\u5F53\u4E8E\u662F\u4E00\u8FB9\u63D0\u9700\u6C42\u4E00\u8FB9\u4EA4\u4ED8\u3002</p></blockquote><h3 id="\u6DF7\u5408\u578B" tabindex="-1"><a class="header-anchor" href="#\u6DF7\u5408\u578B" aria-hidden="true">#</a> \u6DF7\u5408\u578B</h3><blockquote><p>\u6DF7\u5408\u578B\u751F\u547D\u5468\u671F\uFF0C\u76F8\u5F53\u4E8E\u662F\u9884\u6D4B\u578B\u548C\u9002\u5E94\u578B\u7684\u6DF7\u5408\u3002</p></blockquote><blockquote><p>\u5BF9\u4E8E\u786E\u5B9A\u7684\u9700\u6C42\u7528\u9884\u6D4B\u578B\uFF0C\u4E0D\u786E\u5B9A\u7684\u90A3\u4E00\u90E8\u5206\u7528\u9002\u5E94\u578B</p></blockquote><h3 id="\u98CE\u9669" tabindex="-1"><a class="header-anchor" href="#\u98CE\u9669" aria-hidden="true">#</a> \u98CE\u9669</h3><p>\u4ECE\u9884\u6D4B\u578B\u3001\u8FED\u4EE3\u578B\u3001\u589E\u91CF\u578B\u3001\u9002\u5E94\u578B\u3001\u6DF7\u5408\u578B\uFF08\u56E0\u9700\u6C42\u7684\u4E0D\u660E\u786E\uFF0C\u9010\u6E10\u5BF9\u9879\u76EE\u7BA1\u7406\u5931\u63A7\uFF09</p><h2 id="\u4E8B\u4E1A\u73AF\u5883\u56E0\u7D20" tabindex="-1"><a class="header-anchor" href="#\u4E8B\u4E1A\u73AF\u5883\u56E0\u7D20" aria-hidden="true">#</a> \u4E8B\u4E1A\u73AF\u5883\u56E0\u7D20</h2><blockquote><p>\u4E8B\u4E1A\u73AF\u5883\u56E0\u7D20\u662F\u56E2\u961F\u4E0D\u80FD\u63A7\u5236\u4F46\u4F1A\u5BF9\u9879\u76EE\u9020\u6210\u5F71\u54CD\u9650\u5236\u7684\u5916\u90E8\u6761\u4EF6\u7684\u96C6\u5408\u3002</p></blockquote><h3 id="\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a> \u7279\u70B9</h3><ul><li>\u53EF\u80FD\u5BF9\u9879\u76EE\u9020\u6210\u6D88\u6781\u6216\u8005\u79EF\u6781\u7684\u5F71\u54CD</li></ul><h2 id="\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7" tabindex="-1"><a class="header-anchor" href="#\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7" aria-hidden="true">#</a> \u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7</h2><blockquote><p>\u5B83\u662F\u5728\u7BA1\u7406\u9879\u76EE\u7684\u8FC7\u7A0B\u4E2D\u79EF\u7D2F\u7684\u77E5\u8BC6\u548C\u603B\u7ED3\u7684\u7ECF\u9A8C\u6559\u8BAD\u4EE5\u53CA\u9075\u5FAA\u7684\u6D41\u7A0B\u653F\u7B56\u3002</p></blockquote><h3 id="\u7EC4\u6210" tabindex="-1"><a class="header-anchor" href="#\u7EC4\u6210" aria-hidden="true">#</a> \u7EC4\u6210</h3><ul><li><p>\u6D41\u7A0B</p></li><li><p>\u7A0B\u5E8F\u5171\u4EAB\u77E5\u8BC6\u5E93</p></li></ul><blockquote><p>\u662F\u5185\u90E8\u4E0D\u65AD\u603B\u7ED3\u83B7\u5F97\u7684</p></blockquote><h2 id="\u7EC4\u7EC7\u7ED3\u6784\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u7EC4\u7EC7\u7ED3\u6784\u7C7B\u578B" aria-hidden="true">#</a> \u7EC4\u7EC7\u7ED3\u6784\u7C7B\u578B</h2><blockquote><p>\u7EC4\u7EC7\u7ED3\u6784\u6709\u4E09\u7C7B\uFF1A\u804C\u80FD\u578B\u3001\u77E9\u9635\u578B\u3001\u9879\u76EE\u578B\u3002(\u6839\u636E\u9879\u76EE\u7406\u5FF5\u8FDB\u884C\u9012\u589E)</p></blockquote><blockquote><p>\u77E9\u9635\u578B\u53C8\u53EF\u4EE5\u7EC6\u5206\u4E3A\u5F31\u77E9\u9635\u3001\u5E73\u8861\u77E9\u9635\u3001\u5F3A\u77E9\u9635\u3002(\u5C5E\u4E8E\u6839\u636E\u9879\u76EE\u7406\u5FF5\u5728\u4F20\u7EDF\u7BA1\u7406\u6A21\u5F0F\u4E2D\u7684\u5E73\u8861)</p></blockquote><h3 id="\u804C\u80FD\u578B" tabindex="-1"><a class="header-anchor" href="#\u804C\u80FD\u578B" aria-hidden="true">#</a> \u804C\u80FD\u578B</h3><blockquote><p>\u804C\u80FD\u578B\u7EC4\u7EC7\uFF1A\u4F20\u7EDF\u7684\u7EC4\u7EC7\u7ED3\u6784\uFF0C\u6BCF\u4E2A\u90E8\u95E8\u90FD\u6709\u804C\u80FD\u7ECF\u7406\u3002\u7531\u5404\u90E8\u95E8\u804C\u80FD\u7ECF\u7406\u5782\u76F4\u7BA1\u7406\u3002</p></blockquote><blockquote><p>\u56E0\u6B64\u4F1A\u4EA7\u751F\u8DE8\u90E8\u95E8\u7684\u534F\u8C03\u56F0\u96BE\u3002</p></blockquote><h3 id="\u5F31\u77E9\u9635\u578B" tabindex="-1"><a class="header-anchor" href="#\u5F31\u77E9\u9635\u578B" aria-hidden="true">#</a> \u5F31\u77E9\u9635\u578B</h3><blockquote><p>\u5F31\u77E9\u9635\u578B\u7EC4\u7EC7\uFF1A\u9879\u76EE\u7ECF\u7406\u7684\u6743\u9650\u5C0F\u4E8E\u804C\u80FD\u7ECF\u7406\u3002</p></blockquote><blockquote><p>\u51FA\u73B0\u4E86\u8054\u7EDC\u5458\u3001\u534F\u8C03\u5458\u6765\u8D1F\u8D23\u8DE8\u90E8\u95E8\u7684\u6C9F\u901A\u3002</p></blockquote><h3 id="\u5E73\u8861\u77E9\u9635\u578B" tabindex="-1"><a class="header-anchor" href="#\u5E73\u8861\u77E9\u9635\u578B" aria-hidden="true">#</a> \u5E73\u8861\u77E9\u9635\u578B</h3><blockquote><p>\u5E73\u8861\u77E9\u9635\u578B\u7EC4\u7EC7\uFF1A\u9879\u76EE\u7ECF\u7406\u7684\u6743\u529B\u51E0\u4E4E\u7B49\u540C\u4E8E\u804C\u80FD\u7ECF\u7406\uFF0C\u4ED6\u4E0E\u804C\u80FD\u7ECF\u7406\u5171\u540C\u638C\u63E1\u9879\u76EE\u7684\u9884\u7B97\u3002</p></blockquote><blockquote><p>\u5458\u5DE5\u6709\u4E24\u4E2A\u8001\u677F\uFF1A\u804C\u80FD\u7ECF\u7406\u548C\u9879\u76EE\u7ECF\u7406\uFF0C\u6240\u4EE5\u8FD9\u79CD\u7EC4\u7EC7\u67B6\u6784\u6C9F\u901A\u5F88\u590D\u6742\uFF0C\u7BA1\u7406\u96BE\u5EA6\u5927\u3002\u4F46\u662F\u8FD9\u79CD\u7EC4\u7EC7\u7ED3\u6784\uFF0C\u4EBA\u529B\u8D44\u6E90\u7684\u4F7F\u7528\u7387\u8F83\u9AD8\uFF0C\u56E0\u4E3A\u5458\u5DE5\u65E2\u53C2\u4E0E\u4E86\u672C\u804C\u5DE5\u4F5C\uFF0C\u53C8\u53EF\u4EE5\u53C2\u4E0E\u9879\u76EE\u7684\u5DE5\u4F5C\u3002</p></blockquote><h3 id="\u5F3A\u77E9\u9635\u578B" tabindex="-1"><a class="header-anchor" href="#\u5F3A\u77E9\u9635\u578B" aria-hidden="true">#</a> \u5F3A\u77E9\u9635\u578B</h3><blockquote><p>\u5F3A\u77E9\u9635\u578B\u7EC4\u7EC7\uFF1A\u9879\u76EE\u7ECF\u7406\u7684\u6743\u9650\u5927\u4E8E\u804C\u80FD\u7ECF\u7406\uFF0C\u4ED6\u7684\u9876\u5934\u4E0A\u53F8\u4E0D\u518D\u662F\u804C\u80FD\u7ECF\u7406\uFF0C\u800C\u662F\u9879\u76EE\u7ECF\u7406\u7684\u7ECF\u7406\u3002</p></blockquote><blockquote><p>\u5E76\u4E14\u4ED6\u4E00\u4E2A\u4EBA\u638C\u63E1\u9879\u76EE\u7684\u9884\u7B97\uFF0C\u9879\u76EE\u7ECF\u7406\u5168\u804C\u5DE5\u4F5C\u3002</p></blockquote><h3 id="\u6839\u636E\u6743\u9650\u5212\u5206" tabindex="-1"><a class="header-anchor" href="#\u6839\u636E\u6743\u9650\u5212\u5206" aria-hidden="true">#</a> \u6839\u636E\u6743\u9650\u5212\u5206</h3><blockquote><p>\u804C\u80FD\u578B\u3001\u5F31\u77E9\u9635\u578B\u3001\u5E73\u8861\u77E9\u9635\u578B\uFF0C\u9879\u76EE\u7ECF\u7406\u662F\u517C\u804C\u3002</p></blockquote><blockquote><p>\u5F3A\u77E9\u9635\u578B\uFF0C\u9879\u76EE\u7ECF\u7406\u662F\u5168\u804C\u3002</p></blockquote><blockquote><p>\u77E9\u9635\u578B\uFF0C\u7BA1\u7406\u8D44\u6E90\u7684\u662F\u804C\u80FD\u7ECF\u7406\u3002</p></blockquote><h3 id="\u9879\u76EE\u578B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u578B" aria-hidden="true">#</a> \u9879\u76EE\u578B</h3><blockquote><p>\u9879\u76EE\u7ECF\u7406\u6700\u5927\u7A0B\u5EA6\u7684\u638C\u63E1\u4E86\u9879\u76EE\u7684\u56E2\u961F\uFF0C\u5458\u5DE5\u53EA\u9700\u8981\u5411\u81EA\u5DF1\u7684\u9876\u5934\u4E0A\u53F8\uFF0C\u9879\u76EE\u7ECF\u7406\u8FDB\u884C\u6C47\u62A5\u3002</p></blockquote><blockquote><p>\u6240\u4EE5\uFF0C\u8FD9\u79CD\u7EC4\u7EC7\u7ED3\u6784\u6700\u5BB9\u6613\u6C9F\u901A\uFF0C\u4F46\u662F\uFF0C\u8FD9\u79CD\u7EC4\u7EC7\u7ED3\u6784\uFF0C\u5458\u5DE5\u7F3A\u4E4F\u4E8B\u4E1A\u7684\u8FDE\u7EED\u6027\u548C\u4FDD\u969C\uFF0C\u8D44\u6E90\u914D\u7F6E\u91CD\u590D\uFF0C\u4F7F\u7528\u7387\u4F4E\u4E0B\u3002</p></blockquote><h2 id="\u9879\u76EE\u7BA1\u7406\u529E\u516C\u5BA4-pmo" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7BA1\u7406\u529E\u516C\u5BA4-pmo" aria-hidden="true">#</a> \u9879\u76EE\u7BA1\u7406\u529E\u516C\u5BA4(PMO)</h2><blockquote><p>\u9879\u76EE\u7BA1\u7406\u529E\u516C\u5BA4\uFF0C\u82F1\u6587\u7F29\u5199\u662FPMO\u3002PMO\u7684\u4F5C\u7528\u662F\u5BF9\u4E0E\u9879\u76EE\u76F8\u5173\u6CBB\u7406\u8FC7\u7A0B\u8FDB\u884C\u6807\u51C6\u5316\uFF0C\u5E76\u4FC3\u8FDB\u8D44\u6E90\u65B9\u6CD5\u8BBA\u3001\u5DE5\u5177\u548C\u6280\u672F\u5206\u4EAB\u7684\u4E00\u4E2A\u7EC4\u7EC7\u90E8\u95E8\u3002</p></blockquote><p>\u9879\u76EE\u7BA1\u7406\u529E\u516C\u5BA4\u53C2\u4E0E\u9879\u76EE\u7684\u6CBB\u7406\uFF0C\u5E76\u4E14\u6307\u5B9A\u9879\u76EE\u7BA1\u7406\u7684\u6807\u51C6\u3001\u6307\u5B9A\u9879\u76EE\u7BA1\u7406\u7684\u6D41\u7A0B\uFF0C\u8FD8\u80FD\u591F\u628A\u8D44\u6E90\u5728\u591A\u4E2A\u9879\u76EE\u4E4B\u95F4\u8FDB\u884C\u5171\u4EAB\uFF0C\u628A\u597D\u7684\u65B9\u6CD5\u8BBA\u3001\u597D\u7684\u5DE5\u5177\u548C\u6280\u672F\u8FDB\u884C\u5171\u4EAB\u7684\u4E00\u4E2A\u90E8\u95E8\u3002</p><p>PMO\u6839\u636E\u4F01\u4E1A\u7684\u9700\u8981\uFF0C\u53EF\u4EE5\u8BBE\u7F6E\u5728\u4E0D\u540C\u7684\u5C42\u7EA7\uFF0C\u6BD4\u5982\u4F01\u4E1A\u5C42\u7EA7\uFF0C\u4E8B\u4E1A\u90E8\u5C42\u7EA7\u548C\u9879\u76EE\u7EC4\u5C42\u7EA7\u3002</p><p>\u6240\u4EE5\u4ED6\u7684\u804C\u8D23\u53EF\u5927\u53EF\u5C0F\uFF0C\u6839\u636EPMO\u7684\u6743\u529B\u5927\u5C0F\uFF0CPMO\u5206\u4E3A\u4E09\u79CD\u7C7B\u578B\uFF1A\u652F\u6301\u578B\u3001\u63A7\u5236\u578B\u548C\u6307\u4EE4\u578B\u3002</p><h3 id="\u652F\u6301\u578B" tabindex="-1"><a class="header-anchor" href="#\u652F\u6301\u578B" aria-hidden="true">#</a> \u652F\u6301\u578B</h3><p>\u6743\u529B\u6700\u5C0F\u7684\u53EB\u505A\u652F\u6301\u578B\uFF0C\u51E0\u4E4E\u6CA1\u6709\u4EC0\u4E48\u6743\u529B\u62C5\u4EFB\u7684\u662F\u4E00\u4E2A\u987E\u95EE\u7684\u89D2\u8272\uFF0C\u5B83\u53EF\u4EE5\u7ED9\u9879\u76EE\u63D0\u4F9B\u652F\u6301\uFF0C\u53EF\u4EE5\u63D0\u4F9B\u9879\u76EE\u7BA1\u7406\u7684\u6A21\u677F\uFF0C\u4E00\u4E9B\u6700\u4F73\u5B9E\u8DF5\uFF0C\u53EF\u4EE5\u7ED9\u9879\u76EE\u7ECF\u7406\u63D0\u4F9B\u6307\u5BFC\u3001\u8F85\u5BFC\u3001\u57F9\u8BAD\u548C\u76D1\u7763\u3002</p><p>\u6743\u529B\u5267\u4E2D\u7684\u662F\u63A7\u5236\u578B\uFF0C\u65E2\u53EF\u4EE5\u7ED9\u9879\u76EE\u63D0\u4F9B\u6A21\u677F\uFF0C\u63D0\u4F9B\u6700\u4F73\u5B9E\u8DF5\u3001\u63D0\u4F9B\u57F9\u8BAD\u3001\u63D0\u4F9B\u76D1\u7763\uFF0C\u4E5F\u53EF\u4EE5\u6709\u4E00\u5B9A\u5168\u529B\u63A7\u5236\u5355\u4E2A\u9879\u76EE\u3002</p><p>\u6743\u529B\u6700\u5927\u7684\u662F\u6307\u4EE4\u578B\uFF0C\u76F4\u63A5\u7BA1\u7406\u9879\u76EE\uFF0C\u76F4\u63A5\u63A7\u5236\u9879\u76EE\u7684\u4E00\u4E2A\u4EA4\u4ED8\uFF0C\u4ED6\u5BF9\u9879\u76EE\u7684\u7ED3\u679C\u8D1F\u8D23\u4EFB\u3002</p><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><blockquote><p>\u9879\u76EE\u7ECF\u7406</p></blockquote><ol><li><p>\u9879\u76EE\u7ECF\u7406\u5173\u6CE8\u7684\u662F\u5355\u4E2A\u9879\u76EE\u7684\u76EE\u6807\u3002</p></li><li><p>\u9879\u76EE\u7ECF\u7406\u63A7\u5236\u7684\u662F\u5355\u4E2A\u9879\u76EE\u7684\u8D44\u6E90\u3002</p></li><li><p>\u9879\u76EE\u7ECF\u7406\u7BA1\u7406\u7684\u662F\u5355\u4E2A\u9879\u76EE\u7684\u5236\u7EA6\u56E0\u7D20\uFF0C\u6BD4\u5982\u8BF4\u9879\u76EE\u7684\u8303\u56F4\uFF0C\u8FDB\u5EA6\u6210\u672C\u8D28\u91CF\u98CE\u9669\u7B49\u7B49\u3002</p></li></ol><blockquote><p>PMO</p></blockquote><ol><li><p>PMO\u5173\u6CE8\u7684\u662F\u591A\u4E2A\u9879\u76EE\u7684\u76EE\u6807\uFF0C\u7BA1\u7406\u591A\u4E2A\u9879\u76EE\u7684\u53D8\u66F4\u3002</p></li><li><p>PMO\u5173\u6CE8\u7684\u662F\u4F18\u5316\u5229\u7528\u591A\u4E2A\u9879\u76EE\u7684\u8D44\u6E90\u3002</p></li><li><p>PMO\u662F\u7AD9\u5728\u4F01\u4E1A\u7684\u9AD8\u5EA6\u5BF9\u591A\u4E2A\u9879\u76EE\u7684\u5236\u7EA6\u56E0\u7D20\u8FDB\u884C\u8D1F\u8D23\uFF0C\u5B83\u53EF\u4EE5\u53C2\u4E0E\u9879\u76EE\u7EC4\u5408\u7684\u7BA1\u7406\u3002</p></li></ol><h2 id="\u9879\u76EE\u7ECF\u7406\u7684\u6743\u529B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7ECF\u7406\u7684\u6743\u529B" aria-hidden="true">#</a> \u9879\u76EE\u7ECF\u7406\u7684\u6743\u529B</h2><blockquote><p>\u5176\u4E2D\u4E94\u79CD\uFF1A\u4E13\u5BB6\u6743\u529B\u3001\u5956\u52B1\u6743\u529B\u3001\u6B63\u662F\u6743\u529B\u3001\u53C2\u7167\u6027\u6743\u529B\u3001\u60E9\u7F5A\u6743\u529B\u3002</p></blockquote><h3 id="\u4E13\u5BB6\u6743\u529B" tabindex="-1"><a class="header-anchor" href="#\u4E13\u5BB6\u6743\u529B" aria-hidden="true">#</a> \u4E13\u5BB6\u6743\u529B</h3><blockquote><p>\u4E13\u5BB6\u6743\u529B\uFF0C\u4F5C\u4E3A\u6280\u672F\u6216\u8005\u7BA1\u7406\u65B9\u9762\u7684\u4E13\u5BB6\u800C\u4EA7\u751F\u7684\u6743\u529B\u3002</p></blockquote><p>\u6BD4\u5982\uFF0C\u533B\u751F\u5BF9\u75C5\u4EBA\u7684\u6743\u5229\uFF0C\u533B\u751F\u5BF9\u4E8E\u75C5\u4EBA\u6765\u8BF4\uFF0C\u4ED6\u662F\u533B\u5B66\u65B9\u9762\u7684\u4E13\u5BB6\u3002</p><h3 id="\u5956\u52B1\u6743\u529B" tabindex="-1"><a class="header-anchor" href="#\u5956\u52B1\u6743\u529B" aria-hidden="true">#</a> \u5956\u52B1\u6743\u529B</h3><blockquote><p>\u5956\u52B1\u6743\u529B\uFF0C\u6765\u81EA\u4E8E\u9879\u76EE\u7ECF\u7406\u7684\u804C\u4F4D\u3002</p></blockquote><p>\u5982\u679C\u4E0B\u5C5E\u8868\u73B0\u4F18\u826F\uFF0C\u4F60\u53EF\u4EE5\u5BF9\u4ED6\u8FDB\u884C\u5956\u52B1\u3002</p><h3 id="\u6B63\u5F0F\u6743\u529B" tabindex="-1"><a class="header-anchor" href="#\u6B63\u5F0F\u6743\u529B" aria-hidden="true">#</a> \u6B63\u5F0F\u6743\u529B</h3><blockquote><p>\u6B63\u5F0F\u6743\u529B\uFF0C\u4E5F\u53EB\u5408\u6CD5\u6743\u529B\uFF0C\u6765\u81EA\u4E8E\u9879\u76EE\u7ECF\u7406\u7684\u804C\u4F4D\u3002</p></blockquote><p>\u6709\u6743\u5229\u505A\u51FA\u67D0\u79CD\u51B3\u5B9A\uFF0C\u4F46\u662F\u9879\u76EE\u7ECF\u7406\u7684\u6B63\u5F0F\u6743\u529B\u5F80\u5F80\u662F\u4E0D\u8DB3\u7684\uFF0C\u56E0\u4E3A\u6211\u4EEC\u9ED8\u8BA4\u5728\u77E9\u9635\u7EC4\u7EC7\u4E2D\uFF0C\u9879\u76EE\u7ECF\u7406\u4F1A\u53D7\u5230\u804C\u80FD\u7ECF\u7406\u7684\u5236\u7EA6\u3002</p><h3 id="\u53C2\u7167\u6027\u6743\u529B" tabindex="-1"><a class="header-anchor" href="#\u53C2\u7167\u6027\u6743\u529B" aria-hidden="true">#</a> \u53C2\u7167\u6027\u6743\u529B</h3><blockquote><p>\u53C2\u7167\u6027\u6743\u529B\uFF0C\u53C2\u7167\u6027\u6743\u529B\u5206\u4E3A\u4E24\u79CD\uFF0C\u6697\u793A\u6743\u529B\u4EE5\u53CA\u5A01\u671B\u6216\u9B45\u529B\u3002</p></blockquote><p>\u7B2C\u4E00\u4E2A\u6697\u793A\u6743\u529B\u57FA\u4E8E\u9879\u76EE\u7ECF\u7406\u4E8E\u804C\u4F4D\u8F83\u9AD8\u8005\u7684\u5173\u7CFB\u3002\u522B\u4EBA\u4FE1\u4EFB\u4F60\uFF0C\u6B23\u8D4F\u4F60\uFF0C\u4EE5\u4F60\u4E3A\u699C\u6837\u662F\u56E0\u4E3A\u4F60\u548C\u9AD8\u5C42\u7684\u5173\u7CFB\u6BD4\u8F83\u597D\uFF1B\u7B2C\u4E8C\u4E2A\u5A01\u671B\u6216\u9B45\u529B\uFF0C\u6BD4\u5982\u8BF4\u4F60\u6709\u6F02\u4EAE\u7684\u5916\u8C8C\uFF0C\u62E5\u6709\u4EBA\u683C\u7684\u9B45\u529B\uFF0C\u80FD\u591F\u5438\u5F15\u5230\u522B\u4EBA\u3002</p><h3 id="\u60E9\u7F5A\u6743\u529B" tabindex="-1"><a class="header-anchor" href="#\u60E9\u7F5A\u6743\u529B" aria-hidden="true">#</a> \u60E9\u7F5A\u6743\u529B</h3><blockquote><p>\u60E9\u7F5A\u6743\u529B\uFF0C\u5982\u679C\u4E0B\u5C5E\u8868\u73B0\u7684\u4E0D\u597D\uFF0C\u9879\u76EE\u7ECF\u7406\u5BF9\u4ED6\u8FDB\u884C\u5904\u7F5A\uFF0C\u6263\u5DE5\u8D44\uFF0C\u6263\u5956\u91D1\u7B49\u7B49\u3002</p></blockquote><h3 id="\u6743\u529B\u6765\u6E90" tabindex="-1"><a class="header-anchor" href="#\u6743\u529B\u6765\u6E90" aria-hidden="true">#</a> \u6743\u529B\u6765\u6E90</h3><ul><li><p>\u4E13\u5BB6\u6743\u529B\u3001\u53C2\u7167\u6027\u6743\u529B\u6765\u81EA\u4E8E\u9879\u76EE\u7ECF\u7406\u4E2A\u4EBA\u3002</p></li><li><p>\u5956\u52B1\u6743\u529B\u3001\u6B63\u5F0F\u6743\u529B\u3001\u60E9\u7F5A\u6743\u529B\u6765\u81EA\u4E8E\u9879\u76EE\u7ECF\u7406\u7684\u804C\u4F4D\u3002</p></li></ul><h2 id="\u9879\u76EE\u7AE0\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7AE0\u7A0B" aria-hidden="true">#</a> \u9879\u76EE\u7AE0\u7A0B</h2><blockquote><p>\u4EFB\u4F55\u9879\u76EE\u90FD\u9700\u8981\u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B\uFF0C\u7AE0\u7A0B\u662F\u4E3A\u4E86\u7ED9\u9879\u76EE\u7ACB\u9879\uFF0C\u786E\u7ACB\u9879\u76EE\u7684\u6B63\u5F0F\u5730\u4F4D\uFF0C\u5E76\u4E14\u8FD8\u80FD\u591F\u7ED9\u9879\u76EE\u7ECF\u7406\u8FDB\u884C\u6388\u6743\uFF0C\u6388\u6743\u4ED6\u5728\u7EC4\u7EC7\u4E2D\u4F7F\u7528\u8D44\u6E90\u3002</p></blockquote><p>\u7AE0\u7A0B\u6709\u65F6\u5019\u53C8\u53EB\u9879\u76EE\u6279\u51C6\u4E66\u3001\u7ACB\u9879\u4E66\u3001\u7ACB\u9879\u62A5\u544A\u7B49\u7B49\u3002</p><p>\u4E00\u822C\u7531\u9879\u76EE\u7684\u53D1\u8D77\u4EBA\u53D1\u5E03\uFF0C\u4ED6\u4F1A\u5BF9\u4E3B\u8981\u53EF\u4EA4\u4ED8\u6210\u679C\u3001\u91CC\u7A0B\u7891\u4EE5\u53CA\u6BCF\u4F4D\u5E72\u7CFB\u4EBA\u7684\u89D2\u8272\u548C\u804C\u8D23\u8FBE\u6210\u5171\u8BC6\u3002</p><blockquote><p>\u56E0\u6B64\uFF0C\u7AE0\u7A0B\u91CC\u9762\u4F1A\u5305\u62EC\u5F88\u591A\u5185\u5BB9\uFF0C\u5B83\u4E00\u5171\u4F1A\u5305\u542B12\u4E2A\u5185\u5BB9\uFF0C\u91CC\u9762\u90FD\u662F\u5B8F\u89C2\u7684\u3001\u5927\u6982\u7684\u3001\u4E00\u4E9B\u65B9\u5411\u6027\u7684\u5185\u5BB9\u3002</p></blockquote><ol><li><p>\u5305\u62EC\u9879\u76EE\u7684\u76EE\u7684</p></li><li><p>\u53EF\u89C2\u6D4B\u7684\u9879\u76EE\u7684\u76EE\u6807</p></li><li><p>\u9AD8\u5C42\u6B21\u7684\u9700\u6C42(\u9AD8\u5C42\u6B21\u7684\u9700\u6C42\u5C31\u662F\u5B8F\u89C2\u9700\u6C42\u3001\u5927\u6982\u7684\u9700\u6C42\u3001\u4E0D\u662F\u5F88\u5177\u4F53\u7684\u9700\u6C42)</p></li><li><p>\u9AD8\u5C42\u7EA7\u7684\u9879\u76EE\u63CF\u8FF0\u8FB9\u754C\u4EE5\u53CA\u4E3B\u8981\u7684\u53EF\u4EA4\u4ED8\u6210\u679C(\u5B83\u91CC\u9762\u90FD\u662F\u5927\u6982\u7684\u4E00\u4E9B\u8FB9\u754C\uFF0C\u4EE5\u53CA\u4E00\u4E9B\u4E3B\u8981\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\uFF0C\u5E76\u4E0D\u662F\u5168\u90E8\u7684\u53EF\u4EA4\u4ED8\u6210\u679C)</p></li><li><p>\u6574\u4F53\u7684\u9879\u76EE\u98CE\u9669(\u4E5F\u662F\u5927\u6982\u7684\u4E00\u4E9B\u98CE\u9669)</p></li><li><p>\u603B\u4F53\u7684\u91CC\u7A0B\u7891\u8FDB\u5EA6\u8BA1\u5212(\u5927\u6982\u7684\u91CC\u7A0B\u7891\u8FDB\u5EA6\u8BA1\u5212)</p></li><li><p>\u9884\u5148\u6279\u51C6\u7684\u8D22\u52A1\u8D44\u6E90(\u5927\u6982\u7684\u9884\u7B97)</p></li><li><p>\u5173\u952E\u7684\u5E72\u7CFB\u4EBA\u7684\u540D\u5355</p></li><li><p>\u9879\u76EE\u7684\u5BA1\u6279\u8981\u6C42</p></li><li><p>\u9879\u76EE\u7684\u9000\u51FA\u6807\u51C6(\u5728\u4EC0\u4E48\u6837\u7684\u6761\u4EF6\u4E0B\u624D\u80FD\u5173\u95ED\u9879\u76EE\uFF0C\u6216\u8005\u662F\u53D6\u6D88\u9879\u76EE\uFF0C\u4E5F\u4F1A\u5728\u7AE0\u7A0B\u91CC\u9762\u5199\u6E05\u695A)</p></li><li><p>\u59D4\u6D3E\u7684\u9879\u76EE\u7ECF\u7406\u7684\u6743\u8D23\u548C\u804C\u6743</p></li><li><p>\u9879\u76EE\u7684\u53D1\u8D77\u4EBA\u4EE5\u53CA\u9AD8\u7EA7\u7BA1\u7406\u5C42\u7684\u540D\u5B57</p></li></ol><p>\u7AE0\u7A0B\u5F53\u4E2D\u7684\u5185\u5BB9\u90FD\u662F\u5B8F\u89C2\u7684\u3001\u5927\u6982\u7684\u3001\u5927\u4F53\u4E0A\u7684\uFF0C\u6218\u7565\u4E0A\u7684\u4E00\u4E9B\u5185\u5BB9\u5E76\u4E0D\u662F\u7279\u522B\u7684\u5177\u4F53\u3002\u56E0\u4E3A\u505A\u9879\u76EE\u662F\u8D8A\u6765\u8D8A\u6E05\u6670\u3001\u6E10\u8FDB\u660E\u7EC6\u7684\uFF0C\u6240\u4EE5\u4E00\u5F00\u59CB\u5728\u7AE0\u7A0B\u91CC\u5E76\u4E0D\u4F1A\u4E5F\u522B\u7684\u660E\u786E\u3002</p><p>2021/12/24</p>',113);function i(l,r){return h}var t=a(o,[["render",i]]);export{t as default};
